import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Heading from './heading';
import './Styles/topposts.css';

class TopPosts extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            topPost:this.props.toppostsArr[0],
            OtherPost:this.props.toppostsArr.slice(1,4)
        }
    }
    render() {
        return (
            <div id="TopPosts-section" class="subsections">
                <div class="row">
                    <div class="col-md-8">
                        <div class="card" id="toppost-main">
                            <img class="card-img-top" src="../photo-1.jfif" alt="Blog image"></img>
                            <div class="card-img-overlay">
                                <h4 class="card-title">Title of verticle gallery</h4>
                                <p class="card-text">Travel / August 21 2017</p>
                            </div>
                        </div>
                    </div>
                    <div class="col-md-4" id="topPosts-head">
                        <Heading title={this.props.title}></Heading>
                        <div id="topPosts-cards">
                            <>
                            <div class="card">
                                        <img class="card-img" src={this.state.topPost.imgpath} alt="Blog image"></img>
                                        <div class="card-body">
                                            <h4 class="card-title">{this.state.topPost.title}</h4>
                                            <p class="card-text">{this.state.topPost.type} / {this.state.topPost.date}</p>
                                            <p class="card-text">{this.state.topPost.id}</p>
                                        </div>
                            </div>
                            <hr></hr>
                            {this.state.OtherPost.map(post =>
                                    <>
                                    <div class="card">
                                        <img class="card-img" src={post.imgpath} alt="Blog image"></img>
                                        <div class="card-body">
                                            <h4 class="card-title">{post.title}</h4>
                                            <p class="card-text">{post.type} / {post.date}</p>
                                            <p class="card-text">{post.id}</p>
                                        </div>
                                    </div>
                                    <hr></hr>
                                    </>
                            )}
                            </>
                            
                        </div>
                    </div>
                </div>

            </div>
        )
    }
}

export default TopPosts;
