import React from 'react';
import Heading from './heading';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Styles/latestarticle.css';
import Advertisment from './advertisment';


class LatestArticle extends React.Component {
    render() {
        return (
            <div id="LatestArticle-section" class="subsections">
                <Heading title={this.props.title}></Heading>
                <div class="row" id="latestarticle-row">
                    <div class="col-md-8">
                    <div id="latestArticle-cards">
                    {this.props.articleArr.map(article =>
                        <>
                        <hr></hr>
                        <div class="card">
                            <img class="card-img" src={article.imgpath} alt="Blog image"></img>
                            <div class="card-body">
                                <h4 class="card-title">{article.title}</h4>
                                <p class="card-text">{article.description}</p>
                                <p class="card-text">{article.type} <span class="latest-date">/ {article.date}</span></p>
                            </div>
                        </div>
                        </>
                    )}
                </div>
                    </div>
                    <div class="col-md-4">
                        <Advertisment></Advertisment>
                    </div>
                </div>

            </div>
        )
    }

}
export default LatestArticle;
