import React from 'react';
import {data} from './data';
import BlogHeading from '../BlogComp/blogheading';
import BlogSection from '../BlogComp/blogsection';
import 'bootstrap/dist/css/bootstrap.min.css';
class Blog extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            blogCat: this.props.match.params.blog_category,
            blogId:this.props.match.params.blog_id,
            blogData:this.getData()
        }
    }
    getData(){
        var blogtype = this.state.blogId.split("_")[0];
        var blogId = this.state.blogId.split("_")[1];
        if(this.state.blogCat === 'home'){
            return data[blogtype].filter(x => x.id === blogId)[0];
        }
        else{
            return data[this.state.blogCat][blogtype].filter(x => x.id === blogId)[0];
        }
    }
    render(){
        return(
            <React.Fragment>
                <div class="container-fluid">
                    <div class="main-container">
                        <BlogHeading></BlogHeading>
                        <BlogSection></BlogSection>
                    </div>
                </div>
            </React.Fragment>
        )
    }
}
export default Blog;
