import React from 'react';
import HeaderMain from './Components/Header/HeaderMain';
import Main from './Components/MainComp/Main';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';


class App extends React.Component{
  constructor(){
    super();
    this.state = {
      header:[
        {title:"Home",link:"/home"},
        {title:"Bollywood",link:"/bollywood"},
        {title:"Technology",link:"/technology"},
        {title:"Hollywood",link:"/hollywood"},
        {title:"Fitness",link:"/fitness"},
        {title:"Food",link:"/food"}
      ],
      MainBlogArr:[
        {title:"Title of verticle gallery",type:"travel",date:"August 21 2017",imgpath:"../../public/photo-1.jfif"},
        {title:"The Sound cloud You loved is doomed",type:"travel",date:"August 21 2017",imgpath:"../../public/photo-2.jpg"},
        {title:"The Sound cloud You loved is doomed",type:"travel",date:"August 21 2017",imgpath:"../../public/photo-2.jpg"}
      ]
    }
  }

  render(){
    return(
      <React.Fragment>
          <div class="container-fluid">
            <div class="maincontainer">
            <HeaderMain linklist={this.state.header}></HeaderMain>
            <Main cardarr={this.state.MainBlogArr}></Main>
            </div>
        </div>

      </React.Fragment>
    )
  }
}


export default App;
